package com.bookweb.BookService.Controller;

import com.bookweb.BookService.Model.Book;
import com.bookweb.BookService.Model.BookGenre;
import com.bookweb.BookService.Model.User;
import com.bookweb.BookService.Repository.BookRepository;
import com.bookweb.BookService.Repository.UserRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.core.annotation.AuthenticationPrincipal;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.*;

import javax.validation.Valid;
import java.sql.Date;

@Controller
public class ServiceController {

    @Autowired
    private UserRepository userRepository;
    @Autowired
    private BookRepository bookRepository;
    @RequestMapping("/user")
    public String user(@AuthenticationPrincipal User principal)
    {
        return "index2";
    }
    @RequestMapping("/home")
    public String home()
    {
        return "home";
    }
    @PostMapping("/addBook")
    @ResponseBody
    public void addBook(@RequestParam String name, @RequestParam BookGenre genre, @RequestParam Date premiere, String bookPicture)
    {
       // bookPicture = "";
        Book book = new Book(name, genre, premiere, bookPicture);
        bookRepository.insert(book);
    }
}
